mapper.update:
UPDATE transaction
payment_option = #{paymentOption:CHAR}, receiver_name = #{receiverName:VARCHAR}, receiver_phone = #{receiverPhone:VARCHAR}, demailaddr = #{divyAddr:VARCHAR}, dlvy_request = #{divyRequest:VARCHAR}, dlvy_date = #{divyDate:DATE}
WHERE tran_no = #{tranNo}

mapper.select:
SELECT COUNT(*)
FROM(	SELECT tran_no, prod_no, buyer_id, payment_option, receiver_name, receiver_phone, demailaddr, dlvy_request, tran_status_code, order_data, dlvy_date
		FROM transaction
		WHERE buyer_id = #{buyerId}
		ORDER BY tran_no
		) countTable
